int BSums(Mat src)
{
	int counter = 0;
	//迭代器访问像素点
	Mat_<uchar>::iterator it = src.begin<uchar>();
	Mat_<uchar>::iterator itend = src.end<uchar>();
	for (; it != itend; ++it)
	{
		if ((*it) > 0) counter += 1;//二值化后，像素点是0或者255
	}
	return counter;
}

//基于矩形窗口的图像滑动窗口操作,返回值为滑动窗口的数目
//@src 输入图像
//@wnd 输出结果
//@wndSize 滑动窗口的大小
//@ x_percent 滑动窗口在x方向步长的百分比，x_step=x_percent*wndSize.width
//@ y_percent 滑动窗口在y方向步长的百分比，y_step=y_percent*wndSize.height
int slidingWnd(Mat& src, vector<Mat>& wnd, Size& wndSize, double x_percent, double y_percent)
{
	int count = 0;  //记录滑动窗口的数目
					//cvRound()：返回跟参数最接近的整数值，即四舍五入；
					//cvFloor()：返回不大于参数的最大整数值，即向下取整；
					//cvCeil()：返回不小于参数的最小整数值，即向上取整；
	int x_step = cvCeil(x_percent*wndSize.width);
	int y_step = cvCeil(y_percent*wndSize.height);

	int count1 = getTickCount();
	double freq = getTickFrequency();
	//利用窗口对图像进行遍历
	for (int i = 0; i <= src.cols - wndSize.width; i += x_step) //分行  
	{

		for (int j = 0; j <= src.rows - wndSize.height; j += y_step) //分列
		{
			Rect roi(Point(i, j), wndSize);
			rectangle(src, roi, Scalar(0, i * 2, j * 2), 2, 8, 0);
			//窗口
			Mat ROI = src(roi);
			wnd.push_back(ROI);
			count++;
		}
	}
	int count2 = getTickCount();
	double time = (count2 - count1) / freq;
	imshow("wnd", src);

	//cout << "Time=" << time * 100 << "ms" << endl;

	return count;
}

